mapper.select:
select distinct car_series
from db_car_info_all
where car_brand = #{carBrand}
and factory_name = #{factoryName}
order by car_series

select distinct vehicle_type
from db_car_info_all
where car_brand = #{carBrand}
and factory_name = #{factoryName}
and car_series = #{carSeries}
order by vehicle_type

select

from db_car_info_all
where vehicle_type = #{vehicleType}
and car_brand = #{carBrand}
and factory_name = #{factoryName}
and car_series = #{carSeries}
order by leyel_id

select count(car_model)
from db_car_info_all
where vehicle_type = #{vehicleType}
and car_brand = #{carBrand}
and factory_name = #{factoryName}
and car_series = #{carSeries}

select

from db_car_info_all
where vehicle_type like #{vehicleType}

  and car_brand = #{carBrand}


  and factory_name = #{factoryName}


  and car_series = #{carSeries}

order by leyel_id

select count(car_model)
from db_car_info_all
where vehicle_type like #{vehicleType}

  and car_brand = #{carBrand}


  and factory_name = #{factoryName}


  and car_series = #{carSeries}

select a.car_model, a.leyel_id, a.market_name, a.model_year, a.displacement, a.fuel_type
from db_car_info_all a,
  (
  select distinct l_id
  from db_car_maintain_plan
  where maintain_plan_id = #{maintainPlanId}
  and is_deleted = 0
  ) as b
where a.leyel_id=b.l_id
order by a.leyel_id

select car_model, leyel_id, market_name, model_year, displacement, fuel_type
from db_car_info_all
where vehicle_type = #{vehicleType}
and car_brand = #{carBrand}
and factory_name = #{factoryName}
and car_series = #{carSeries}
and leyel_id not in
(select distinct l_id
from db_model_maintain_plan mp, db_car_maintain_plan cp
where mp.car_brand = #{carBrand} and mp.company = #{factoryName}
and mp.car_series = #{carSeries} and mp.car_model = #{vehicleType}
and mp.id=cp.maintain_plan_id and mp.is_deleted = 0 and cp.is_deleted = 0)
order by leyel_id

select COUNT(1)
from db_car_info_all
where leyel_id = #{leyelId,jdbcType=VARCHAR}
